import React, { useState } from "react";

export default function DebitCredit() {
  let initalData = {
    amount: 0,
    txType: "",
  };
  const [txnDetails, setTxnDetails] = useState(initalData);
  const handleChange = (name, val) => {

    setTxnDetails((prevalue) => {
      return {
        ...prevalue, // Spread Operator
        [name]: value,
      };
    });
  };
  return (
    <div className="container">
      <div id="formId" style={{ width: "250px" }}>
        <div className="row">
          <div className="col">
            <div>
              <label htmlFor="inputPassword5" className="form-label">
                Enter Amount htmlFor Debit/Credit
              </label>
              <input
                type="text"
                id="amount"
                className="form-control"
                aria-describedby="passwordHelpBlock"
                onKeyDown={(e) => {
                  handleChange("amount", e.target.value);
                }}
              />
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col">
            <div className="form-check">
              <input
                className="form-check-input"
                type="radio"
                name="flexRadioDefault"
                id="radio2"
                value={"Debit"}
                onClick={(e) => {
                  //   console.log(e.target.value, "  ", e.target.name);
                  handleChange("txType", e.target.value);
                }}
              />
              <label className="form-check-label" htmlFor="flexRadioDefault2">
                Debit
              </label>
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col">
            <div className="form-check">
              <input
                className="form-check-input"
                type="radio"
                name="flexRadioDefault"
                value={"Credit"}
                id="radio1"
                onClick={(e) => {
                  //   console.log(e.target.value, "  ", e.target.name);
                  handleChange("txType", e.target.value);
                }}
              />
              <label className="form-check-label" htmlFor="flexRadioDefault1">
                Credit
              </label>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col">
            <button
              className="btn btn-primary"
              onClick={() => {
                console.log("on Click: ", txnDetails);
              }}
            >
              Click
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
